sys_sg_data_item.query_condition_script:
(function getQueryString(input) {
    //Type appropriate comment here, and begin script below
    var owner_input = input.OwnerParam;
    var short_desc = input.ShortDescriptionParam;
    var queryString = '';

    queryString = queryString+'state!=1^state!=2^state!=6'+'^';

    if(!gs.nil(owner_input))
        queryString = queryString+'u_reference_1LIKE' + owner_input+'^';

    if(!gs.nil(short_desc))
        queryString = queryString+'short_descriptionLIKE' + short_desc+'^';

    queryString=queryString+'ORDERBYDESCplanned_start';

    return queryString;
})(input);
