sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
    var current = new GlideRecord('x_snc_econow_philanthrophy');
    current.get(input.sys_id);
    var currentUser = gs.getUser();
    var watch_list = current.watch_list.toString().split(",");
    var index = watch_list.indexOf(gs.getUserID()); // <-- Not supported in <IE9
    if (index !== -1) {
        watch_list.splice(index, 32);
        current.watch_list = watch_list.join(",").toString();
		gs.addInfoMessage(gs.getMessage('You are deregistered.'));
    }
    current.update();
})(input);
